-- найти точки в полигоне
select st_astext(
  st_multi( st_makepolygon ( -- st_convexhull 
      'LINESTRING( -73.9956663036519 40.6754306136443, -73.9956663036519 40.6754306136443, -73.9956663036519 40.6754306136443, -73.9956663036519 40.6754306136443, -73.9860495142482 40.5993560879652, -73.870648041403 40.7514184329975, -73.8266855755572 40.6504293761137, -73.9956663036519 40.6754306136443 )'::geometry
    )
  )
) as text;

select 
  id_crime as id,
  age_group as age,
  cr_type as type,
  time,
  date,
  st_x(ny_crimes.geom) as lng, 
  st_y(ny_crimes.geom) as lat
from 
  ny_crimes 
where st_intersects(
  ny_crimes.geom, 
  st_multi( st_makepolygon ( -- st_convexhull 
      'LINESTRING( -73.9956663036519 40.6754306136443, -73.9956663036519 40.6754306136443, -73.9956663036519 40.6754306136443, -73.9956663036519 40.6754306136443, -73.9860495142482 40.5993560879652, -73.870648041403 40.7514184329975, -73.8266855755572 40.6504293761137, -73.9956663036519 40.6754306136443 )'::geometry
    )
  )
)